import axios from 'axios';

const api = axios.create({
  baseURL: 'http://localhost:8088/api/task',
});

export const listarTasks = async () => {
  try {
    const response = await api.get('/listar-task');
    const data = response.data;
    data.sort((a, b) => a.id - b.id); // Ordena as tarefas por ID de forma crescente
    return data;
  } catch (error) {
    console.error('Erro ao listar tasks', error);
    throw error;
  }
};

export const adicionarTask = async (task) => {
  try {
    const response = await api.post('/adicionar-task', null, {
      params: {
        descricao: task.descricao,
        completa: task.completa,
        priority: task.priority,
        prazo: task.prazo
      }
    });
    return response.data;
  } catch (error) {
    console.error('Erro ao adicionar task', error);
    throw error;
  }
};

export const editarTask = async (id, task) => {
  try {
    const response = await api.put(`/editar-task`, null, {
      params: {
        id: id,
        descricao: task.descricao,
        completa: task.completa,
        priority: task.priority
      }
    });
    return response.data;
  } catch (error) {
    console.error('Erro ao editar task', error);
    throw error;
  }
};

export const recuperarTaskPorId = async (id) => {
  try {
    const response = await api.get(`/recuperar-task-por-id?id=${id}`);
    return response.data;
  } catch (error) {
    console.error('Erro ao recuperar task', error);
    throw error;
  }
};

export const deletarTaskPorId = async (id) => {
  try {
    const response = await api.delete(`/deletar-task-por-id?id=${id}`);
    return response.data;
  } catch (error) {
    console.error('Erro ao deletar task', error);
    throw error;
  }
};

export default api;
