import React, { useState, useEffect } from "react";
import { TodoForm } from "./TodoForm";
import { TodoList } from "./TodoList";
import { listarTasks, adicionarTask, editarTask, deletarTaskPorId } from '../service/TaskService';

export const TodoWrapper = () => {
  const [todos, setTodos] = useState([]);
  const [error, setError] = useState(null); // Estado para armazenar a mensagem de erro

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const data = await listarTasks();
        setTodos(data);
        setError(null); // Limpar o erro se a chamada for bem-sucedida
      } catch (error) {
        console.error('Erro ao buscar tasks', error);
        setError(error.response ? error.response.data : error.message); // Definir a mensagem de erro específica
      }
    };

    fetchTasks();
  }, []);

  const addTodo = async (description, complete, priority, deadline) => {
    const newTask = {
      descricao: description,
      completa: complete,
      prazo: deadline,
      priority: priority
    };

    try {
      await adicionarTask(newTask);
      const data = await listarTasks();
      setTodos(data);
      setError(null); // Limpar o erro se a chamada for bem-sucedida
    } catch (error) {
      console.error('Erro ao adicionar task', error);
      setError(error.response ? error.response.data : error.message); // Definir a mensagem de erro específica
    }
  };

  const deleteTodo = async (id) => {
    try {
      await deletarTaskPorId(id);
      const data = await listarTasks();
      setError(null); // Limpar o erro se a chamada for bem-sucedida
      setTodos(data);
    } catch (error) {
      console.error('Erro ao deletar task', error);
      setError(error.response ? error.response.data : error.message); // Definir a mensagem de erro específica
    }
  };

  const toggleComplete = async (id) => {
    const taskToToggle = todos.find((todo) => todo.id === id);
    const updatedTask = {
      ...taskToToggle,
      completed: !taskToToggle.completed
    };
    try {
      await editarTask(id, updatedTask);
      const data = await listarTasks();
      setTodos(data);
      setError(null); // Limpar o erro se a chamada for bem-sucedida
    } catch (error) {
      console.error('Erro ao editar task', error);
      setError(error.response ? error.response.data : error.message); // Definir a mensagem de erro específica
    }
  };

  return (
    <div className="TodoWrapper">
      <h1>Lista de Tarefas</h1>
      {error && <div className="error">{error}</div>} {/* Exibir a mensagem de erro */}
      <TodoForm addTodo={addTodo} />
      {todos.map((todo) => (
        <TodoList key={todo.id} task={todo} deleteTodo={deleteTodo} toggleComplete={toggleComplete} />
      ))}
    </div>
  );
};

export default TodoWrapper;
