import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash, faPenToSquare } from '@fortawesome/free-solid-svg-icons';

export const TodoList = ({ task, deleteTodo, toggleComplete, editTodo }) => {
  const [showDetails, setShowDetails] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [editDescription, setEditDescription] = useState(task.description || '');
  const [editPriority, setEditPriority] = useState(task.priority || 'baixa');

  const toggleDetails = () => {
    setShowDetails(!showDetails);
  };

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleSave = () => {
    editTodo(task.id, editDescription, task.completed, editPriority);
    setIsEditing(false);
  };

  const handleCancel = () => {
    setIsEditing(false);
    setEditDescription(task.description || '');
    setEditPriority(task.priority || 'baixa');
  };

  return (
    <div className='Todo'>
      {isEditing ? (
        <div>
          <input
            type="text"
            value={editDescription}
            onChange={(e) => setEditDescription(e.target.value)}
            className="todo-input"
          />
          <select
            value={editPriority}
            onChange={(e) => setEditPriority(e.target.value)}
            className="todo-select"
          >
            <option value="alta">Alta</option>
            <option value="media">Média</option>
            <option value="baixa">Baixa</option>
          </select>
          <button className="todo-btn" onClick={handleSave}>Salvar</button>
          <button className="todo-btn" onClick={handleCancel}>Cancelar</button>
        </div>
      ) : (
        <div>
          <p className={`${task.completed ? 'completed' : 'incompleted'}`} onClick={() => toggleComplete(task.id)}>
            Descrição: {task.description}
          </p>
          {showDetails && (
            <div className="todo-details">
              ID: {task.id} <br />
              Prazo: {task.deadlineDate ? task.deadlineDate : 'Sem prazo'} <br />
              Prioridade: {task.priority} <br />
            </div>
          )}
          <button className="toggle-details-btn" onClick={toggleDetails}>
            {showDetails ? 'Ver menos' : 'Ver mais'}
          </button>
          <div className='divIcon'>
            <FontAwesomeIcon className='edit-icon' icon={faPenToSquare} onClick={handleEdit} />
            <FontAwesomeIcon className='delete-icon' icon={faTrash} onClick={() => deleteTodo(task.id)} />
          </div>
        </div>
      )}
    </div>
  );
};

export default TodoList;
