import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash, faPenToSquare } from '@fortawesome/free-solid-svg-icons';

export const TodoList = ({ task, deleteTodo, toggleComplete, editTodo }) => {
  const [showDetails, setShowDetails] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [description, setDescription] = useState(task.description);
  const [complete, setComplete] = useState(task.completed);
  const [priority, setPriority] = useState(task.priority);

  const toggleDetails = () => {
    setShowDetails(!showDetails);
  };

  const handleSave = () => {
    editTodo(task.id, description, complete, priority);
    setIsEditing(false);
  };

  const handleCancel = () => {
    setIsEditing(false);
    setDescription(task.description);
    setComplete(task.completed);
    setPriority(task.priority);
  };

  const handleEditClick = () => {
    setIsEditing(true);
  };

  return (
    <div className='Todo'>
      <div className='todo-content'>
        <p className={`${task.completed ? 'completed' : 'incompleted'}`}>
          Descrição: {task.description}
        </p>
        <div className='divIcon'>
          <FontAwesomeIcon className='edit-icon' icon={faPenToSquare} onClick={handleEditClick} />
          <FontAwesomeIcon className='delete-icon' icon={faTrash} onClick={() => deleteTodo(task.id)} />
        </div>
      </div>
      {showDetails && (
        <div className="todo-details">
          ID: {task.id} <br />
          Prazo: {task.deadlineDate ? task.deadlineDate : 'Sem prazo'} <br />
          Prioridade: {task.priority} <br />
        </div>
      )}
      <button className="toggle-details-btn" onClick={toggleDetails}>
        {showDetails ? 'Ver menos' : 'Ver mais'}
      </button>
      {isEditing && (
        <div className='edit-form'>
          <input
            type='text'
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            className='todo-input'
          />
          <div className='edit-options'>
            <label className="todo-checkbox-label">
              Completa?
              <input
                type='checkbox'
                checked={complete}
                onChange={(e) => setComplete(e.target.checked)}
                className='todo-checkbox'
              />
            </label>
            <select
              value={priority}
              onChange={(e) => setPriority(e.target.value)}
              className='todo-select'
            >
              <option value="alta">Alta</option>
              <option value="media">Média</option>
              <option value="baixa">Baixa</option>
            </select>
          </div>
          <div className='edit-buttons'>
            <button className="todo-btn" onClick={handleSave}>Salvar</button>
            <button className="todo-btn" onClick={handleCancel}>Cancelar</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default TodoList;
