import React, { useState, useEffect } from "react";
import axios from "axios";
import { TodoForm } from "./TodoForm";
import { TodoList } from "./TodoList";

export const TodoWrapper = () => {
  const [todos, setTodos] = useState([]);

  const fetchTodos = async () => {
    try {
      const response = await axios.get('https://lab-des-soft.onrender.com/task/listar-task');
      setTodos(response.data);
    } catch (error) {
      console.error("Erro ao listar tarefas:", error);
    }
  };

  useEffect(() => {
    fetchTodos();
  }, []);

  const addTodo = async (description) => {
    try {
      await axios.post('https://lab-des-soft.onrender.com/task/adicionar-task', {
        descricao: description,
        completa: false,
        priority: "LOW", // Ajuste conforme necessário
        prazo: null
      });
      fetchTodos();
    } catch (error) {
      console.error("Erro ao adicionar tarefa:", error);
    }
  };

  const deleteTodo = async (id) => {
    try {
      await axios.delete(`https://lab-des-soft.onrender.com/task/deletar-task-por-id?id=${id}`);
      fetchTodos();
    } catch (error) {
      console.error("Erro ao deletar tarefa:", error);
    }
  };

  const toggleComplete = async (id) => {
    const todo = todos.find((todo) => todo.id === id);
    if (todo) {
      try {
        await axios.put('https://lab-des-soft.onrender.com/task/editar-task', {
          id: id,
          descricao: todo.descricao,
          completa: !todo.completa,
          priority: todo.priority
        });
        fetchTodos();
      } catch (error) {
        console.error("Erro ao completar tarefa:", error);
      }
    }
  };

  return (
    <div className="TodoWrapper">
      <h1>Lista de Tarefas</h1>
      <TodoForm addTodo={addTodo} />
      {todos.map((todo) => (
        <TodoList key={todo.id} task={todo} deleteTodo={deleteTodo} toggleComplete={toggleComplete} />
      ))}
    </div>
  );
};
export default TodoWrapper;
